mapper.update:
update sample_templates
set
    name = case id

        when #{entity.id} then #{entity.name}

    end,
    type = case id

        when #{entity.id} then #{entity.type}

    end,
    version = case id

        when #{entity.id} then #{entity.version}

    end,
    data = case id

        when #{entity.id} then #{entity.data}

    end,
    description = case id

        when #{entity.id} then #{entity.description}

    end,
    is_active = case id

        when #{entity.id} then #{entity.isActive}

    end,
    is_deleted = case id

        when #{entity.id} then #{entity.isDeleted}

    end,
    updated_time = case id

        when #{entity.id} then #{entity.updatedTime}

    end
where id in

    #{entity.id}
