mapper.select:
select 
from sample_users
where creator_id = #{creatorId}
  and sample_id = #{sampleId}

mapper.update:
update sample_users
set
    name = case id

        when #{entity.id} then #{entity.name}

    end,
    data = case id

        when #{entity.id} then #{entity.data}

    end,
    source = case id

        when #{entity.id} then #{entity.source}

    end,
    version_injected = case id

        when #{entity.id} then #{entity.versionInjected}

    end,
    updated_time = case id

        when #{entity.id} then #{entity.updatedTime}

    end
where id in

    #{entity.id}
